def ner(text):
    new_text = text
    for key, value in dataset.items():
        if key in text: 
            new_text = new_text.replace(
                    key, 
                    f"""<span style="color: blue; cursor: pointer;" 
                              title="{value}" class='choosen_word' >{key}</span>"""
                    )        
    return new_text


